package com.jianye.smart.module.workbench.attendance.adapter;import java.io.File;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.GridView;import android.widget.ImageView;import com.androidquery.AQuery;import com.androidquery.callback.BitmapAjaxCallback;import com.movit.platform.framework.utils.SharedPreUtils;import com.jianye.smart.R;public class AttendancePicGridAdapter extends BaseAdapter {	private Context context;	private LayoutInflater mInflater;	private List<String> mData;	GridView gridView;	SharedPreUtils spUtil;	AQuery aQuery;	public AttendancePicGridAdapter(Context context, List<String> mData) {		super();		this.context = context;		this.mData = mData;		this.mInflater = LayoutInflater.from(context);		spUtil = new SharedPreUtils(context);		aQuery = new AQuery(context);	}	@Override	public int getCount() {		return 3;	}	@Override	public Object getItem(int arg0) {		return mData.get(arg0);	}	@Override	public long getItemId(int arg0) {		return arg0;	}	@Override	public View getView(final int postion, View converView, ViewGroup arg2) {		ViewHolder holder = null;		if (converView == null) {			holder = new ViewHolder();			converView = mInflater.inflate(R.layout.attendance_gridview_item,					arg2, false);			holder.photo = (ImageView) converView					.findViewById(R.id.gridview_item_img);			converView.setTag(holder);		} else {			holder = (ViewHolder) converView.getTag();		}		AQuery aq = aQuery.recycle(converView);		BitmapAjaxCallback callback = new BitmapAjaxCallback();		callback.animation(AQuery.FADE_IN_FILE);		callback.rotate(true);		if (mData.size() > postion && mData.get(postion) != null) {			aq.id(holder.photo).image(new File(mData.get(postion)), true, 256,					callback);		} else {			aq.id(holder.photo).image(R.drawable.pic_add);		}		return converView;	}	public final class ViewHolder {		public ImageView photo;	}}