package com.movit.platform.common.helper;import android.content.Context;import android.database.SQLException;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;public class DatabaseHepler extends SQLiteOpenHelper {    public static final int DB_VERSION = 10;    public static final String Table_SESSION = "session";    private static final String DROP_TABLE = "DROP TABLE IF EXISTS ";    public DatabaseHepler(Context context, String dbName) {        super(context, dbName, null, DB_VERSION);    }    @Override    public void onOpen(SQLiteDatabase db) throws SQLException {        super.onOpen(db);    }    @Override    public void onCreate(SQLiteDatabase db) {        db.beginTransaction();        db.execSQL("CREATE TABLE IF NOT EXISTS "                + Table_SESSION                + " (_id INTEGER PRIMARY KEY AUTOINCREMENT, sid TEXT,ctype TEXT)");        db.setTransactionSuccessful();        db.endTransaction();    }    @Override    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {        db.execSQL(DROP_TABLE + Table_SESSION);        onCreate(db);    }    @Override    public synchronized SQLiteDatabase getWritableDatabase() {        return super.getWritableDatabase();    }    @Override    public SQLiteDatabase getReadableDatabase() {        return super.getReadableDatabase();    }}