package com.movit.platform.innerea.db;import android.content.Context;import com.movit.platform.framework.core.db.MFDBHelper;import java.util.HashMap;import java.util.Map;public class DBHepler extends MFDBHelper {    public static final int DB_VERSION = 11;    public static final String Table_SESSION = "session";    private static final String TABLE_SESSION_SQL = "(_id INTEGER PRIMARY KEY AUTOINCREMENT, sid TEXT,ctype TEXT)";    public static final String Table_LOG = "gps_log";    public static final String Table_LOG_SQL = "(_id INTEGER PRIMARY KEY AUTOINCREMENT, day TEXT,time TEXT)";    public DBHepler(Context context, String dbName) {        super(context, dbName, null, DB_VERSION);    }    @Override    protected Map<String, String> initTable() {        Map<String, String> tableMap = new HashMap<>();        tableMap.put(Table_SESSION, TABLE_SESSION_SQL);        tableMap.put(Table_LOG, Table_LOG_SQL);        return tableMap;    }}